export type FlatVariant = 'primary' | 'secondary' | 'neutral' | 'success' | 'info' | 'danger' | 'warning';

export type OutlineVariant = 'outline-base' | 'outline-success' | 'outline-danger' | 'outline-warning' | 'outline-info' | 'outline-primary';

export type ColorVariants = FlatVariant | OutlineVariant;
